def contract_deploy(bytecode, fromAddress):
    bytecode = bytecode
    transactionHash = platon.sendTransaction(
        {
            "from": fromAddress,
            "gas": 1000000,
            "gasPrice": 1000000000,
            "data": bytecode,
        }
    )
    transactionHash = HexBytes(transactionHash).hex().lower()
    return transactionHash

